import React, { useState } from "react";
import "./components/Users/AddUser";
import AddUser from "./components/Users/AddUser";
import UserList from "./components/Users/UserList";
import NoTitle from "./components/Users/NoTitle";

function App() {
  const [isValid, setIsValid] = useState(true);

  const SetFalse = () =>{
    setIsValid(false);
  }

  const SetTrue = () =>{
    setIsValid(true);
  }

  const userData = [
    { id: 'e1', name: "Max", age: 25 },
    { id: 'e2', name: "Good", age: 23 },
  ];

  const [isUserData, setIsUserData] = useState(userData);

  const UserDataHandler = (newData) => {
    setIsUserData((prevData) => {
      return [...prevData, newData];
    });
  };

  let editing1 = <NoTitle sendValid={SetTrue}></NoTitle>
  let editing2 = '';

  if(isValid){
  editing1 = <AddUser onAddUser={UserDataHandler} sendValid={SetFalse}></AddUser>;
  editing2 = <UserList sendData={isUserData} ></UserList>;
  }
  return (
    <div>
      {editing1}
      {editing2}
    </div>
  );
}

export default App;
