import React, { useState } from "react";
const AddUser = (props) => {
  const [newUserName, setNewUserName] = useState("");
  const [newUserAge, setNewUserAge] = useState("");
  let blank1 = "";
  let blank2 = "";

  const UserNameHandler = (event) => {
    blank1 = event.target.value;
    setNewUserName(event.target.value);
  };

  const UserAgeHandler = (event) => {
    blank2 = event.target.value;
    setNewUserAge(event.target.value);
  };
  const UserDataHandler = (event) => {
    event.preventDefault();
    if (newUserName.length === 0) {
      props.sendValidTitle();
      return;
    }

    if (newUserAge < 0) {
      props.sendValidNumber();
      return;
    }

    blank1 = '';;
    blank2 = '';
    const newData = {
      id: Math.random(),
      name: newUserName,
      age: newUserAge,
    };
    props.onAddUser(newData);
  };

  return (
    <div>
      <form onSubmit={UserDataHandler}>
        <label>Username</label>
        <input type="text" value={blank1} onChange={UserNameHandler}></input>
        <label>Age(Year)</label>
        <input type="number" value={blank2} onChange={UserAgeHandler}></input>
        <button type="submit">Add User</button>
      </form>
    </div>
  );
};

export default AddUser;
