import React, { useState } from "react";
import Card from '../UI/Card';
import classes from './AddUser.module.css';
import Button from "../UI/Button";
const AddUser = (props) => {
  const [newUserName, setNewUserName] = useState("");
  const [newUserAge, setNewUserAge] = useState("");

  const UserNameHandler = (event) => {
    setNewUserName(event.target.value);
  };

  const UserAgeHandler = (event) => {
    setNewUserAge(event.target.value);
  };
  const addUserHandler = (event) => {
    event.preventDefault();
    if (newUserName.trim().length === 0) {
      props.sendValidTitle();
      return;
    }

    if (+newUserAge < 1) {
      props.sendValidNumber();
      return;
    }

    const newData = {
      id: Math.random(),
      name: newUserName,
      age: newUserAge,
    };
    props.onAddUser(newData);
    setNewUserName('');
    setNewUserAge('');
  };

  return (
    <Card className={classes.input}>
      <form onSubmit={addUserHandler}>
        <label htmlFor="username">Username</label>
        <input id="username" value={newUserName}type="text" onChange={UserNameHandler}></input>
        <label htmlFor="age">Age(Year)</label>
        <input id="age" value={newUserAge} type="number" onChange={UserAgeHandler}></input>
        <Button type="submit">Add User</Button>
      </form>
    </Card>
  );
};

export default AddUser;
