import React, { useState } from "react";
const AddUser = (props) => {
  const [newUserName, setNewUserName] = useState("");
  const [newUserAge, setNewUserAge] = useState("");

  const UserNameHandler = (event) => {
    setNewUserName(event.target.value);
  };

  const UserAgeHandler = (event) => {
    setNewUserAge(event.target.value);
  };
  const UserDataHandler = (event) => {
    event.preventDefault();
    if (newUserName.length === 0) {
      props.sendValidTitle();
      return;
    }

    if (newUserAge < 0) {
      props.sendValidNumber();
      return;
    }

    const newData = {
      id: Math.random(),
      name: newUserName,
      age: newUserAge,
    };
    props.onAddUser(newData);
  };

  return (
    <form onSubmit={UserDataHandler}>
      <label htmlFor="username">Username</label>
      <input id="username" type="text" onChange={UserNameHandler}></input>
      <label htmlFor="age">Age(Year)</label>
      <input id="age" type="number" onChange={UserAgeHandler}></input>
      <button type="submit">Add User</button>
    </form>
  );
};

export default AddUser;
